                              onKeyPress={(e) => {
                                if (e.key === 'Enter') {
                                  const value = (e.target as HTMLInputElement).value.trim();
                                  if (value && !productForm.sizes.includes(value)) {
                                    setProductForm({
                                      ...productForm,
                                      sizes: [...productForm.sizes, value]
                                    });
                                    (e.target as HTMLInputElement).value = '';
                                  }
                                }
                              }}
                              data-testid="input-custom-size"
                            />
