                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={(e) => {
                                const input = (e.target as HTMLButtonElement).previousSibling as HTMLInputElement;
                                const value = input.value.trim();
                                if (value && !productForm.sizes.includes(value)) {
                                  setProductForm({
                                    ...productForm,
                                    sizes: [...productForm.sizes, value]
