import { useState, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search, X } from "lucide-react";
import { type Category, type ProductWithCategory } from "@shared/schema";
import ProductCard from "@/components/ProductCard";

export default function Shop() {
  const [selectedCategory, setSelectedCategory] = useState<string>("");
  const [searchQuery, setSearchQuery] = useState<string>("");

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
  });

  const { data: allProducts = [], isLoading } = useQuery<ProductWithCategory[]>({
    queryKey: ['/api/products', { categoryId: selectedCategory || undefined, is_active: true }],
  });

  // Filter products based on search query
  const products = useMemo(() => {
    if (!searchQuery.trim()) {
      return allProducts;
    }

    const query = searchQuery.toLowerCase().trim();
    return allProducts.filter(product => 
      product.name.toLowerCase().includes(query) ||
      product.description?.toLowerCase().includes(query) ||
      product.material?.toLowerCase().includes(query) ||
      product.category?.name.toLowerCase().includes(query)
    );
  }, [allProducts, searchQuery]);

  return (
    <div className="pt-24 pb-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

        {/* Page Header */}
        <div className="text-center mb-16">
          <h1 className="text-4xl font-light tracking-wide mb-4">SHOP</h1>
          <p className="text-gray-600 font-light max-w-2xl mx-auto">
            Discover our complete collection of premium men's innerwear designed for comfort, style, and sophistication.
          </p>
        </div>

        {/* Search Bar */}
        <div className="max-w-2xl mx-auto mb-12">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="h-5 w-5 text-gray-400" />
            </div>
            <input
              id="shop-search-input"
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search products, materials, categories..."
              className="w-full pl-10 pr-12 py-3 border border-gray-200 rounded-sm focus:outline-none focus:ring-1 focus:ring-luxury-black focus:border-luxury-black text-sm"
              data-testid="input-search"
            />
            {searchQuery && (
              <button
                onClick={() => setSearchQuery("")}
                className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-400 hover:text-gray-600"
                data-testid="button-clear-search"
              >
                <X className="h-5 w-5" />
              </button>
            )}
          </div>
          {searchQuery && (
            <div className="mt-2 text-sm text-gray-600">
              {products.length > 0 ? (
                <span>Found {products.length} product{products.length !== 1 ? 's' : ''} for "{searchQuery}"</span>
              ) : (
                <span>No products found for "{searchQuery}"</span>
              )}
            </div>
          )}
        </div>

        {/* Category Filter */}
        <div className="flex flex-wrap justify-center gap-4 mb-12">
          <button
            onClick={() => {
              setSelectedCategory("");
              setSearchQuery(""); // Clear search when changing category
            }}
            className={`px-6 py-2 text-sm font-medium tracking-wide transition-colors ${
              selectedCategory === "" 
                ? "bg-luxury-black text-white" 
                : "border border-luxury-black text-luxury-black hover:bg-luxury-black hover:text-white"
            }`}
            data-testid="filter-all"
          >
            ALL
          </button>
          {categories.map((category) => (
            <button
              key={category.id}
              onClick={() => {
                setSelectedCategory(category.id);
                setSearchQuery(""); // Clear search when changing category
              }}
              className={`px-6 py-2 text-sm font-medium tracking-wide transition-colors ${
                selectedCategory === category.id 
                  ? "bg-luxury-black text-white" 
                  : "border border-luxury-black text-luxury-black hover:bg-luxury-black hover:text-white"
              }`}
              data-testid={`filter-${category.slug}`}
            >
              {category.name.toUpperCase()}
            </button>
          ))}
        </div>

        {/* Products Grid */}
        {isLoading ? (
          <div className="text-center py-16">
            <p className="text-gray-600 font-light">Loading products...</p>
          </div>
        ) : products.length === 0 ? (
          <div className="text-center py-16">
            <h3 className="text-lg font-medium tracking-wide mb-2">NO PRODUCTS FOUND</h3>
            <p className="text-gray-600 font-light">
              {searchQuery ? (
                <>
                  No products match your search for "{searchQuery}". 
                  <button 
                    onClick={() => setSearchQuery("")}
                    className="ml-1 text-luxury-black underline hover:no-underline"
                    data-testid="button-clear-search-results"
                  >
                    Clear search
                  </button>
                  {" "}or try different keywords.
                </>
              ) : (
                "Try adjusting your filters or browse all products."
              )}
            </p>
          </div>
        ) : (
          <>
            <div className="flex justify-between items-center mb-8">
              <p className="text-sm text-gray-600 font-light">
                Showing {products.length} product{products.length !== 1 ? 's' : ''}
              </p>
              <select className="text-sm border border-luxury-muted px-3 py-2 focus:outline-none focus:border-luxury-black" data-testid="select-sort">
                <option value="name">Sort by Name</option>
                <option value="price-low">Price: Low to High</option>
                <option value="price-high">Price: High to Low</option>
                <option value="newest">Newest First</option>
              </select>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6" data-testid="shop-product-grid">
              {products.map((product) => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          </>
        )}

        {/* Recently Viewed Section */}
        <section className="mt-24 pt-16 border-t border-luxury-muted">
          <div className="text-center mb-12">
            <h2 className="text-2xl font-light tracking-wide mb-4">RECENTLY VIEWED</h2>
            <p className="text-gray-600 font-light">Products you've recently looked at</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {products.slice(0, 4).map((product) => (
              <ProductCard key={`recent-${product.id}`} product={product} />
            ))}
          </div>
        </section>

      </div>
    </div>
  );
}

client/src/pages/Wishlist.tsx.content:
import { useWishlist } from "@/hooks/useWishlist";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Heart, Trash2, ShoppingCart } from "lucide-react";
import { useCart } from "@/hooks/useCart";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";

export default function Wishlist() {
  const { wishlistItems, isLoading, removeFromWishlist, isRemovingFromWishlist } = useWishlist();
  const { addToCart, isAddingToCart } = useCart();
  const { toast } = useToast();
  const [addingToCartId, setAddingToCartId] = useState<string | null>(null);

  const handleAddToCart = async (productId: string, productName: string) => {
    setAddingToCartId(productId);

    try {
      await addToCart({
        productId,
        size: "M", // Default size - in a real app, this would be selectable
        color: "Black", // Default color - in a real app, this would be selectable
        quantity: 1,
      });

      toast({
        title: "Added to cart",
        description: `${productName} has been added to your cart.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Please select size and color on the product page.",
        variant: "destructive",
      });
    } finally {
      setAddingToCartId(null);
    }
  };

  if (isLoading) {
    return (
      <div className="pt-24 pb-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-center min-h-96">
            <div className="text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-luxury-black mx-auto mb-4"></div>
              <p className="text-gray-600 font-light">Loading your wishlist...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (wishlistItems.length === 0) {
    return (
      <div className="pt-24 pb-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h1 className="text-4xl font-light tracking-wide mb-4">WISHLIST</h1>
            <p className="text-gray-600 font-light max-w-2xl mx-auto">
              Save your favorite items for later
            </p>
          </div>

          <div className="text-center min-h-96 flex items-center justify-center">
            <div>
              <Heart className="h-16 w-16 text-gray-300 mx-auto mb-6" />
              <h2 className="text-2xl font-light tracking-wide mb-4 text-gray-800">Your wishlist is empty</h2>
              <p className="text-gray-600 font-light mb-8 max-w-md mx-auto">
                Start browsing our collection and add items you love to your wishlist.
              </p>
              <Link href="/shop">
                <Button 
                  className="bg-luxury-black text-white hover:bg-gray-800 px-8 py-3 text-sm font-medium tracking-wide"
                  data-testid="button-shop-now"
                >
                  SHOP NOW
                </Button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="pt-24 pb-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

        {/* Page Header */}
        <div className="text-center mb-16">
          <h1 className="text-4xl font-light tracking-wide mb-4">WISHLIST</h1>
          <p className="text-gray-600 font-light max-w-2xl mx-auto">
            {wishlistItems.length} {wishlistItems.length === 1 ? 'item' : 'items'} saved
          </p>
        </div>

        {/* Wishlist Items Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8" data-testid="wishlist-grid">
          {wishlistItems.map((item) => (
            <div key={item.id} className="group relative" data-testid={`wishlist-item-${item.productId}`}>

              {/* Remove Button */}
              <button
                onClick={() => removeFromWishlist(item.productId!)}
                disabled={isRemovingFromWishlist}
                className="absolute top-4 right-4 z-10 p-2 bg-white/90 backdrop-blur-sm rounded-full shadow-sm hover:bg-white transition-all duration-200"
                data-testid={`button-remove-${item.productId}`}
              >
                <Trash2 size={16} className="text-gray-600" />
              </button>

              {/* Product Image */}
              <Link href={`/products/${item.product.slug}`}>
                <div className="aspect-[3/4] overflow-hidden mb-4 cursor-pointer">
                  <img 
                    src={item.product.images?.[0] || 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400'} 
                    alt={item.product.name}
                    className="w-full h-full object-cover group-hover:opacity-95 transition-opacity" 
                  />
                </div>
              </Link>

              {/* Product Details */}
              <div className="space-y-3">
                <div>
                  <Link href={`/products/${item.product.slug}`}>
                    <h3 className="text-lg font-light tracking-wide hover:text-gray-600 transition-colors" data-testid={`product-name-${item.productId}`}>
                      {item.product.name}
                    </h3>
                  </Link>
                </div>

                <div className="flex items-center justify-between">
                  <div>
                    <span className="text-lg font-medium" data-testid={`product-price-${item.productId}`}>
                      LKR {item.product.price}
                    </span>
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="space-y-2">
                  <Link href={`/products/${item.product.slug}`}>
                    <Button 
                      variant="outline" 
                      className="w-full text-sm font-medium tracking-wide"
                      data-testid={`button-view-product-${item.productId}`}
                    >
                      VIEW PRODUCT
                    </Button>
                  </Link>

                  <Button
                    onClick={() => handleAddToCart(item.productId!, item.product.name)}
                    disabled={addingToCartId === item.productId || isAddingToCart}
                    className="w-full bg-luxury-black text-white hover:bg-gray-800 text-sm font-medium tracking-wide"
                    data-testid={`button-add-to-cart-${item.productId}`}
                  >
                    {addingToCartId === item.productId ? (
                      "ADDING..."
                    ) : (
                      <>
                        <ShoppingCart size={16} className="mr-2" />
                        QUICK ADD TO CART
                      </>
                    )}
                  </Button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Continue Shopping */}
        <div className="text-center mt-16 pt-8 border-t border-luxury-muted">
          <Link href="/shop">
            <Button 
              variant="outline"
              className="px-8 py-3 text-sm font-medium tracking-wide"
              data-testid="button-continue-shopping"
            >
              CONTINUE SHOPPING
            </Button>
          </Link>
        </div>

      </div>
    </div>
  );
}

